INSERT INTO `blog_article` VALUES (5, '大叔', '小心点进来，可能会吓着你', 'https://www.yanshisan.cn/image/cover/2019122812254868.png', '<p></p><p></p><h1>这里是图片测试</h1><p><br></p><pre><code class=\"hljs javascript\"><span class=\"hljs-comment\">/**</span><br><span class=\"hljs-comment\">     * 文章图片</span><br><span class=\"hljs-comment\">     *</span><br><span class=\"hljs-comment\">     * <span class=\"hljs-doctag\">@param </span>files</span><br><span class=\"hljs-comment\">     * <span class=\"hljs-doctag\">@return</span></span><br><span class=\"hljs-comment\"><span class=\"hljs-doctag\">     </span>*/</span><br>    @PostMapping(<span class=\"hljs-string\">\"uploadPictures\"</span>)<br>    @ResponseBody<br>    public <span class=\"hljs-built_in\">Map</span>&lt;<span class=\"hljs-built_in\">String</span>, <span class=\"hljs-built_in\">Object</span>&gt; uploadPictures(@RequestParam(<span class=\"hljs-string\">\"article\"</span>) MultipartFile[] files) {<br>        <span class=\"hljs-built_in\">String</span> bucketName = propertiesUtils.getBucketName();<br>        <span class=\"hljs-built_in\">String</span> endpoint = propertiesUtils.getEndpoint();<br>        OSS oss = getOss();<br>        HashMap&lt;<span class=\"hljs-built_in\">String</span>, <span class=\"hljs-built_in\">Object</span>&gt; map = <span class=\"hljs-keyword\">new</span> HashMap&lt;&gt;();<br>        ArrayList&lt;<span class=\"hljs-built_in\">String</span>&gt; paths = <span class=\"hljs-keyword\">new</span> ArrayList&lt;&gt;();<br>        <span class=\"hljs-keyword\">try</span> {<br>            <span class=\"hljs-keyword\">for</span> (MultipartFile file : files) {<br>                <span class=\"hljs-comment\">//文件路径加名称</span><br><span class=\"hljs-comment\">                String filename = \"article\" + \"/\" + new DateTime().toString(\"yyyy/MM/dd\") + \"/\" + UUID.randomUUID().toString() + file.getOriginalFilename();//设置日期格式</span><br><span class=\"hljs-comment\">                InputStream inputStream = file.getInputStream();</span><br><span class=\"hljs-comment\">                oss.putObject(bucketName, filename, inputStream);</span><br><span class=\"hljs-comment\">                String filePath = \"https://\" + bucketName + \".\" + endpoint + \"/\" + filename;</span><br><span class=\"hljs-comment\">                paths.add(filePath);</span><br><span class=\"hljs-comment\">            }</span><br><span class=\"hljs-comment\">        } catch (IOException e) {</span><br><span class=\"hljs-comment\">            e.printStackTrace();</span><br><span class=\"hljs-comment\">            map.put(\"errno\", 1);</span><br><span class=\"hljs-comment\">        }</span><br><span class=\"hljs-comment\">        map.put(\"errno\", 0);</span><br><span class=\"hljs-comment\">        map.put(\"data\", paths);</span><br><span class=\"hljs-comment\">        return map;</span><br><span class=\"hljs-comment\">    }</span></code></pre><p><iframe src=\"//player.bilibili.com/player.html?aid=371387418&amp;bvid=BV1rZ4y1u76A&amp;cid=213363532&amp;page=1\" scrolling=\"no\" border=\"0\" frameborder=\"no\" framespacing=\"0\" allowfullscreen=\"true\"> </iframe></p><p><br></p><p></p><p><br></p><p></p><p><br></p>', '超哥', '自创', '0', '2020-07-09 14:13:06', '2020-07-24 10:09:52');
